mapper.select:
SELECT d.*,c.`name` AS categoryName FROM sky_take_out.dish d LEFT OUTER JOIN sky_take_out.category c ON d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc

mapper.update:
update sky_take_out.dish
name=#{name}, category_id=#{categoryId}, price=#{price}, image=#{image}, description=#{description}, update_Time=#{updateTime}, update_User=#{updateUser}, status=#{status},
where id = #{id}

mapper.select:
select * from sky_take_out.dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc
